import React, { forwardRef } from 'react';

// --- Helper Functions & Type Definitions ---

// A simple utility for conditional class names, to replicate the `h` function.
const cn = (...classes: string[]) => classes.filter(Boolean).join(' ');

export interface IconProps {
  size?: number;
  weight?: 'thin' | 'light' | 'regular' | 'bold' | 'fill' | 'duotone';
  color?: string;
  alt?: string;
  mirrored?: boolean;
  className?: string;
  style?: React.CSSProperties;
  viewBox?: string;
  vectorSizeOverride?: number;
  children?: React.ReactNode;
}

// --- Base Wrapper Components ---

/**
 * A base wrapper for simple, single-path icons.
 * This is a direct reconstruction of the `Ag` component from the original code.
 */
const Wrapper = ({
  size = 20,
  vectorSizeOverride,
  className,
  alt,
  viewBox = '0 0 20 20',
  children,
}: IconProps) => {
  const sizeMap: any = { 12: 16, 16: 20, 20: 20, 24: 24, 28: 28, 32: 32 };
  const vectorSize = vectorSizeOverride || sizeMap[size] || size;

  const svg = (
    <svg
      width={vectorSize}
      height={vectorSize}
      viewBox={viewBox}
      fill="currentColor"
      xmlns="http://www.w3.org/2000/svg"
      className="shrink-0"
      aria-label={alt}
      aria-hidden={!alt}
    >
      {children}
    </svg>
  );

  return vectorSizeOverride ? (
    svg
  ) : (
    <div
      className={cn('flex items-center justify-center', className || '')}
      style={{ width: size, height: size }}
    >
      {svg}
    </div>
  );
};

/**
 * A base wrapper for multi-weight icons (like Phosphor icons).
 * This is a direct reconstruction of the `d` component from the original code.
 */
const PhosphorIcon = forwardRef<SVGSVGElement, IconProps & { weights: Map<string, React.ReactNode> }>(
  ({ weights, weight = 'regular', color, size = '1em', mirrored = false, alt, ...rest }, ref) => {
    const svgContent = weights.get(weight) || weights.get('regular');

    return (
      <svg
        ref={ref}
        xmlns="http://www.w3.org/2000/svg"
        width={size}
        height={size}
        fill={color || 'currentColor'}
        viewBox="0 0 256 256"
        style={mirrored ? { transform: 'scaleX(-1)' } : {}}
        aria-label={alt}
        aria-hidden={!alt}
        {...rest}
      >
        <rect width="256" height="256" fill="none" />
        {svgContent}
      </svg>
    );
  }
);
PhosphorIcon.displayName = 'PhosphorIcon';


